$(function(){
	$('#_TB').addClass("hasjs");

	/* navigation submenus*/
	$('#nav > li').hover(
		function(){
			$('#nav > li').removeClass("hover");
			$(this).addClass("hover");
		},
		function(){
			$('#nav > li').removeClass("hover");
		}
	);
	
	/* region picker*/
	$('#region-picker:not(ul a)').toggle(
		function(){
			$(this).removeClass("active");
			$(this).addClass("active");
		},
		function(){
			$(this).removeClass("active");
		}
	);
	$('#region-picker ul li:not(.selected) a').bind("click",function() {
		document.location.href=$(this).attr("href");
	})
	
});
